import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { AiOutlineUser, AiOutlineLogout } from 'react-icons/ai'; // Importing icons for user and logout

const Navbar = ({ user }) => {
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const navigate = useNavigate();

    // Toggle the dropdown visibility
    const toggleDropdown = () => {
        setIsDropdownOpen((prev) => !prev);
    };

    // Close the dropdown when clicking outside
    const closeDropdown = () => {
        setIsDropdownOpen(false);
    };

    // Handle Edit Profile navigation
    const handleEditProfile = () => {
        navigate('/edit-profile');
        closeDropdown();
    };

    // Handle Sign Out
    const handleSignOut = () => {
        localStorage.removeItem('token');
        navigate('/'); // Redirect to login page after sign out
        closeDropdown();
    };

    return (
        <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
            <div className="text-xl font-bold">Dashboard</div>

            {/* Profile Section */}
            <div className="relative">
                {/* Profile button (avatar or icon) */}
                <button
                    onClick={toggleDropdown}
                    className="flex items-center space-x-2 p-2 bg-gray-800 rounded-full hover:bg-gray-700 focus:outline-none"
                >
                    {/* Display avatar or fallback icon */}
                    {user?.avatar ? (
                        <img
                            src={user.avatar}
                            alt="User Avatar"
                            className="w-8 h-8 rounded-full"
                        />
                    ) : (
                        <AiOutlineUser className="text-white w-8 h-8" />
                    )}
                </button>

                {/* Dropdown menu */}
                {isDropdownOpen && (
                    <div className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-md overflow-hidden z-10">
                        <ul>
                            <li>
                                <button
                                    onClick={handleEditProfile}
                                    className="block px-4 py-2 text-gray-800 hover:bg-gray-100 w-full text-left"
                                >
                                    Edit Profile
                                </button>
                            </li>
                            <li>
                                <button
                                    onClick={handleSignOut}
                                    className="block px-4 py-2 text-gray-800 hover:bg-gray-100 w-full text-left flex items-center"
                                >
                                    Sign Out
                                    <AiOutlineLogout className="ml-2" />
                                </button>
                            </li>
                        </ul>
                    </div>
                )}
            </div>

            {/* Close dropdown when clicking outside */}
            {isDropdownOpen && (
                <div
                    className="fixed inset-0 bg-transparent"
                    onClick={closeDropdown}
                ></div>
            )}
        </nav>
    );
};

export default Navbar;
