import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:5000/api' }); // Replace with your backend URL

// Add authorization token to each request
API.interceptors.request.use((config) => {
    const token = localStorage.getItem('token'); // Replace with your token storage logic
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
});

// Fetch all notes
export const fetchNotes = () => API.get('/notes');

// Create a new note
export const createNote = (noteData) => API.post('/notes', noteData);

// Update an existing note
export const updateNote = (id, noteData) => API.put(`/notes/${id}`, noteData);

// Delete a note
export const deleteNote = (id) => API.delete(`/notes/${id}`);

export default API;
