import React from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../api/api';
import AuthForm from '../components/AuthForm';

const SignupPage = () => {
    const navigate = useNavigate();

    const handleSignup = async (formData) => {
        try {
            await API.post('/auth/register', formData); // API call to register the user
            alert('Sign up successful! Please log in.'); // Show popup for successful signup
            navigate('/login'); // Redirect to the login page
        } catch (error) {
            console.error('Signup failed:', error.response?.data || error.message);
            alert('Signup failed. Please try again.'); // Show error if signup fails
        }
    };

    return (
        <div className="flex items-center justify-center h-screen bg-gray-100">
            <AuthForm onSubmit={handleSignup} isSignup />
        </div>
    );
};

export default SignupPage;
