import React, { useEffect, useState } from 'react';
import API from '../api/api';
import { useNavigate } from 'react-router-dom';
import NotesList from '../components/NotesList';

const DashboardPage = () => {
    const [notes, setNotes] = useState([]);
    const [user, setUser] = useState(null);
    const [isDropdownOpen, setIsDropdownOpen] = useState(false); // Control dropdown visibility
    const navigate = useNavigate();

    // Fetch notes and user profile
    useEffect(() => {
        const fetchNotes = async () => {
            try {
                const { data } = await API.get('/notes');
                setNotes(data);
            } catch (error) {
                console.error('Failed to fetch notes:', error.response?.data || error.message);
            }
        };

        const fetchUserProfile = async () => {
            try {
                const { data } = await API.get('/profile');
                setUser(data);
            } catch (error) {
                console.error('Failed to fetch user profile:', error.response?.data || error.message);
            }
        };

        fetchNotes();
        fetchUserProfile();
    }, []);

    // Handle sign-out
    const handleSignOut = () => {
        localStorage.removeItem('token');
        navigate('/login'); // Redirect to login page after sign out
    };

    // Toggle dropdown visibility
    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    // Handle edit profile
    const handleEditProfile = () => {
        navigate('/edit-profile'); // Redirect to Edit Profile page
    };

    return (
        <div>
            {/* Navbar */}
            <nav className="bg-gray-800 text-white p-4 flex justify-between items-center">
                <div className="text-xl font-bold">Dashboard</div>
                <div className="relative">
                    {/* Profile button */}
                    {user && (
                        <button
                            onClick={toggleDropdown}
                            className="flex items-center space-x-2 p-2 bg-gray-800 rounded-full hover:bg-gray-700"
                        >
                            {user.avatar ? (
                                <img
                                    src={user.avatar}
                                    alt="User Avatar"
                                    className="w-8 h-8 rounded-full"
                                />
                            ) : (
                                <div className="w-8 h-8 bg-gray-400 rounded-full"></div>
                            )}
                            <span>{user.name || user.email}</span>
                        </button>
                    )}

                    {/* Dropdown menu */}
                    {isDropdownOpen && (
                        <div className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-md overflow-hidden z-10">
                            <ul>
                                <li>
                                    <button
                                        onClick={handleEditProfile}
                                        className="block px-4 py-2 text-gray-800 hover:bg-gray-100 w-full text-left"
                                    >
                                        Edit Profile
                                    </button>
                                </li>
                                <li>
                                    <button
                                        onClick={handleSignOut}
                                        className="block px-4 py-2 text-gray-800 hover:bg-gray-100 w-full text-left"
                                    >
                                        Sign Out
                                    </button>
                                </li>
                            </ul>
                        </div>
                    )}
                </div>
            </nav>

            {/* Main Content */}
            <div className="p-6">
                <h2 className="text-2xl font-bold mb-6">Your Notes</h2>
                <NotesList notes={notes} />
            </div>
        </div>
    );
};

export default DashboardPage;
