import React, { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import API from '../api/api';

const NoteEditorPage = () => {
    const { id } = useParams(); // Get the note ID for editing
    const [formData, setFormData] = useState({ title: '', content: '', category: '' });
    const [loading, setLoading] = useState(!!id); // If there's an ID, set loading to true
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        if (id) {
            // Fetch the note for editing
            const fetchNote = async () => {
                try {
                    const { data } = await API.get(`/notes/${id}`);
                    setFormData(data);
                } catch (error) {
                    setError(error.response?.data?.message || 'Failed to fetch note.');
                } finally {
                    setLoading(false);
                }
            };
            fetchNote();
        }
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (id) {
                // Update existing note
                await API.put(`/notes/${id}`, formData);
                alert('Note updated successfully!');
            } else {
                // Create a new note
                await API.post('/notes', formData);
                alert('Note created successfully!');
            }
            navigate('/dashboard');
        } catch (error) {
            setError(error.response?.data?.message || 'Failed to save note.');
        }
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p className="text-red-500">{error}</p>;

    return (
        <div className="flex items-center justify-center min-h-screen bg-gray-100">
            <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-lg w-96">
                <h2 className="text-2xl font-bold mb-4">{id ? 'Edit Note' : 'Create Note'}</h2>
                <div className="mb-4">
                    <label className="block text-gray-700">Title</label>
                    <input
                        type="text"
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                        required
                    />
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700">Content</label>
                    <textarea
                        name="content"
                        value={formData.content}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                        required
                    ></textarea>
                </div>
                <div className="mb-4">
                    <label className="block text-gray-700">Category</label>
                    <input
                        type="text"
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                        className="w-full border p-2 rounded"
                        required
                    />
                </div>
                <button
                    type="submit"
                    className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 w-full"
                >
                    {id ? 'Update Note' : 'Create Note'}
                </button>
            </form>
        </div>
    );
};

export default NoteEditorPage;
