import React, { useEffect, useState } from 'react';
import API from '../api/api';
import NotesList from '../components/NotesList';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
    const [notes, setNotes] = useState([]);
    const [user, setUser] = useState(null); // Store user info
    const [isDropdownOpen, setIsDropdownOpen] = useState(false); // For controlling dropdown visibility
    const navigate = useNavigate();

    // Fetch the notes and profile data
    useEffect(() => {
        const fetchNotes = async () => {
            try {
                const { data } = await API.get('/notes');
                setNotes(data);
            } catch (error) {
                console.error('Failed to fetch notes:', error.response?.data || error.message);
            }
        };

        const fetchUserProfile = async () => {
            try {
                const { data } = await API.get('/profile'); // Adjust based on your API endpoint
                setUser(data); // Set user profile data (name, email, avatar, etc.)
            } catch (error) {
                console.error('Failed to fetch user profile:', error.response?.data || error.message);
            }
        };

        fetchNotes();
        fetchUserProfile();
    }, []);

    // Handle sign-out
    const handleSignOut = () => {
        // Clear token or user data from localStorage or context
        localStorage.removeItem('token');
        navigate('/login'); // Redirect to login page
    };

    // Toggle dropdown visibility
    const toggleDropdown = () => {
        setIsDropdownOpen(!isDropdownOpen);
    };

    // Handle Edit Profile
    const handleEditProfile = () => {
        // Redirect to the Edit Profile page (you need to implement this page)
        navigate('/edit-profile');
    };

    return (
        <div className="flex">
            {/* Sidebar */}
            <div className="w-64 bg-gray-800 text-white h-screen p-6">
                <h2 className="text-2xl font-bold mb-6">Dashboard</h2>
                <nav>
                    <ul>
                        <li>
                            <a href="/dashboard" className="block py-2 px-4 hover:bg-gray-700">Your Notes</a>
                        </li>
                        <li>
                            <a href="/profile" className="block py-2 px-4 hover:bg-gray-700">Profile</a>
                        </li>
                    </ul>
                </nav>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-6">
                <div className="flex justify-between mb-6">
                    <h2 className="text-2xl font-bold">Your Notes</h2>

                    {/* Profile and Dropdown */}
                    {user && (
                        <div className="relative">
                            <button
                                onClick={toggleDropdown}
                                className="flex items-center space-x-2 p-2 bg-gray-800 text-white rounded-full hover:bg-gray-700"
                            >
                                <span>{user.name || user.email}</span>
                                {user.avatar ? (
                                    <img
                                        src={user.avatar}
                                        alt="User Avatar"
                                        className="w-8 h-8 rounded-full"
                                    />
                                ) : (
                                    <div className="w-8 h-8 bg-gray-400 rounded-full"></div>
                                )}
                            </button>

                            {/* Dropdown Menu */}
                            {isDropdownOpen && (
                                <div className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-md overflow-hidden z-10">
                                    <ul>
                                        <li>
                                            <button
                                                onClick={handleEditProfile}
                                                className="block px-4 py-2 text-gray-800 hover:bg-gray-100 w-full text-left"
                                            >
                                                Edit Profile
                                            </button>
                                        </li>
                                        <li>
                                            <button
                                                onClick={handleSignOut}
                                                className="block px-4 py-2 text-gray-800 hover:bg-gray-100 w-full text-left"
                                            >
                                                Sign Out
                                            </button>
                                        </li>
                                    </ul>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                <NotesList notes={notes} />

                {/* User Profile Section */}
                {user && (
                    <div className="mt-8 p-6 bg-gray-100 rounded-lg shadow-lg">
                        <h3 className="text-xl font-semibold mb-4">Profile Information</h3>
                        <div className="flex items-center mb-4">
                            <img
                                src={user.avatar || '/default-avatar.png'} // Fallback to default image if no avatar
                                alt="User Avatar"
                                className="w-16 h-16 rounded-full mr-4"
                            />
                            <div>
                                <p className="text-lg font-medium">{user.name || 'No Name Provided'}</p>
                                <p className="text-gray-500">{user.email}</p>
                            </div>
                        </div>
                        <div className="mt-4">
                            <p className="text-sm text-gray-700">Joined on: {user.createdAt}</p>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default DashboardPage;
