import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchNotes, deleteNote } from '../api/api';
import Navbar from '../components/Navbar';

const DashboardPage = () => {
    const [notes, setNotes] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const getNotes = async () => {
            try {
                const { data } = await fetchNotes();
                setNotes(data);
            } catch (error) {
                console.error('Failed to fetch notes:', error.response?.data || error.message);
            }
        };
        getNotes();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this note?')) {
            try {
                await deleteNote(id);
                setNotes(notes.filter((note) => note._id !== id));
                alert('Note deleted successfully!');
            } catch (error) {
                console.error('Failed to delete note:', error.response?.data || error.message);
            }
        }
    };

    return (
        <div>
            <Navbar />
            <div className="p-6">
                <h2 className="text-2xl font-bold mb-6">Your Notes</h2>
                <button
                    onClick={() => navigate('/note/create')}
                    className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
                >
                    Create New Note
                </button>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-4">
                    {notes.map((note) => (
                        <div key={note._id} className="bg-white p-4 rounded shadow hover:shadow-lg">
                            <h3 className="font-bold">{note.title}</h3>
                            <p className="text-gray-600">{note.content}</p>
                            <div className="flex justify-between mt-4">
                                <button
                                    onClick={() => navigate(`/note/edit/${note._id}`)}
                                    className="text-blue-500 underline"
                                >
                                    Edit
                                </button>
                                <button
                                    onClick={() => handleDelete(note._id)}
                                    className="text-red-500 underline"
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default DashboardPage;
