import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchNotes, deleteNote } from '../api/api';

const DashboardPage = () => {
    const [notes, setNotes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const getNotes = async () => {
            try {
                setLoading(true);
                const { data } = await fetchNotes();
                setNotes(data || []);
            } catch (error) {
                setError(error.response?.data?.message || 'Failed to fetch notes.');
            } finally {
                setLoading(false);
            }
        };
        getNotes();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this note?')) {
            try {
                await deleteNote(id);
                setNotes((prevNotes) => prevNotes.filter((note) => note._id !== id));
                alert('Note deleted successfully!');
            } catch (error) {
                alert(error.response?.data?.message || 'Failed to delete note.');
            }
        }
    };

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;

    return (
        <div className="p-6">
            <h2 className="text-2xl font-bold mb-4">Your Notes</h2>
            <button onClick={() => navigate('/note/create')} className="bg-blue-600 text-white px-4 py-2 rounded">
                Create New Note
            </button>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-6">
                {notes.map((note) => (
                    <div key={note._id} className="p-4 border rounded">
                        <h3 className="font-bold">{note.title}</h3>
                        <p>{note.content}</p>
                        <div className="flex justify-between mt-2">
                            <button onClick={() => navigate(`/note/edit/${note._id}`)}>Edit</button>
                            <button onClick={() => handleDelete(note._id)} className="text-red-500">
                                Delete
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default DashboardPage;
