import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchNotes, deleteNote } from '../api/api';
import Navbar from '../components/Navbar';

const DashboardPage = () => {
    const [notes, setNotes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const getNotes = async () => {
            try {
                setLoading(true);
                const { data } = await fetchNotes();
                setNotes(data || []); // Ensure data is always an array
            } catch (error) {
                setError(error.response?.data?.message || 'Failed to fetch notes.');
                console.error('Error fetching notes:', error);
            } finally {
                setLoading(false);
            }
        };
        getNotes();
    }, []);

    const handleDelete = async (id) => {
        if (window.confirm('Are you sure you want to delete this note?')) {
            try {
                await deleteNote(id);
                setNotes((prevNotes) => prevNotes.filter((note) => note._id !== id));
                alert('Note deleted successfully!');
            } catch (error) {
                alert(error.response?.data?.message || 'Failed to delete note. Please try again.');
                console.error('Error deleting note:', error);
            }
        }
    };

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <p className="text-gray-500 text-xl">Loading notes...</p>
            </div>
        );
    }

    if (error) {
        return (
            <div className="flex items-center justify-center min-h-screen">
                <p className="text-red-500 text-xl">{error}</p>
            </div>
        );
    }

    return (
        <div>
            <Navbar />
            <div className="p-6">
                <div className="flex justify-between items-center mb-6">
                    <h2 className="text-2xl font-bold">Your Notes</h2>
                    <button
                        onClick={() => navigate('/note/create')}
                        className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700"
                    >
                        Create New Note
                    </button>
                </div>
                {notes.length > 0 ? (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {notes.map((note) => (
                            <div
                                key={note._id}
                                className="bg-white p-4 rounded shadow hover:shadow-lg transition"
                            >
                                <h3 className="font-bold text-gray-800">{note.title}</h3>
                                <p className="text-gray-600">{note.content}</p>
                                <p className="text-gray-400 mt-2">Category: {note.category}</p>
                                <div className="flex justify-between mt-4">
                                    <button
                                        onClick={() => navigate(`/note/edit/${note._id}`)}
                                        className="text-blue-500 underline"
                                    >
                                        Edit
                                    </button>
                                    <button
                                        onClick={() => handleDelete(note._id)}
                                        className="text-red-500 underline"
                                    >
                                        Delete
                                    </button>
                                </div>
                            </div>
                        ))}
                    </div>
                ) : (
                    <p className="text-gray-500 mt-6 text-center">
                        No notes found. Create your first note!
                    </p>
                )}
            </div>
        </div>
    );
};

export default DashboardPage;
