import React, { useEffect, useState } from 'react';
import API from '../api/api';
import Navbar from '../components/Navbar'; // Import Navbar
import NotesList from '../components/NotesList'; // Import NotesList
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
    const [notes, setNotes] = useState([]);
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch notes and user profile when the page loads
        const fetchNotes = async () => {
            try {
                const { data } = await API.get('/notes');
                setNotes(data);
            } catch (error) {
                console.error('Failed to fetch notes:', error.response?.data || error.message);
            }
        };

        const fetchUserProfile = async () => {
            try {
                const { data } = await API.get('/profile');
                setUser(data);
            } catch (error) {
                console.error('Failed to fetch user profile:', error.response?.data || error.message);
            }
        };

        fetchNotes();
        fetchUserProfile();
    }, []);

    // Navigate to create note page
    const handleCreateNote = () => {
        navigate('/create-note');
    };

    // Handle delete note
    const handleDeleteNote = async (id) => {
        try {
            await API.delete(`/notes/${id}`);
            setNotes((prev) => prev.filter((note) => note._id !== id)); // Update state to reflect deletion
        } catch (error) {
            console.error('Failed to delete note:', error.response?.data || error.message);
        }
    };

    return (
        <div>
            <Navbar user={user} />
            <div className="p-6">
                <h2 className="text-2xl font-bold mb-6">Your Notes</h2>

                {/* Button to create a new note */}
                <button
                    onClick={handleCreateNote}
                    className="bg-blue-500 text-white py-2 px-4 rounded mb-6 hover:bg-blue-600"
                >
                    Create New Note
                </button>

                {/* List of notes */}
                <NotesList notes={notes} onDeleteNote={handleDeleteNote} />
            </div>
        </div>
    );
};

export default DashboardPage;
